import React, { useEffect, useState } from 'react'
import axios from 'axios'

/*const api = {
  key: '1147083ab9a3466d9332ac26b5a5c2d0',
  base: 'https://api.openweathermap.org/data/2.5/',
}*/

export const News = () => {
  const [news, setNews] = useState([])
  const [newFilter, setNewFilter] = useState('')

  useEffect(() => {
    fetch(
      'https://newsapi.org/v2/top-headlines?country=us&apiKey=1147083ab9a3466d9332ac26b5a5c2d0'
    )
      .then((res) => res.json())
      .then((res) => setNews(res.data.article))
  }, [])

  const GetArticles = (url) => {
    // Make HTTP reques with Axios
    axios
      .get(`https://newsapi.org/v2/top-headlines?country=us&apiKey=1147083ab9a3466d9332ac26b5a5c2d0`)
      .then(res => {
        const articles = res.data.articles;
        // Set state with result
        console.log(articles);
        this.setState({ articles: articles });
      })
      .catch(error => {
        console.log(error);
      });
  }

  const NewsArticle = ({ article }) => {
    const mapNews = () =>
      article
        .map((news) => (
          <div key={news}>
            <p>{news.title}</p>
          </div>
        ))
    return <div>{mapNews()}</div>
  }

  /*const NewsAPI = require('newsapi');
    const newsapi = new NewsAPI('1147083ab9a3466d9332ac26b5a5c2d0');*/
  // To query sources
  // All options are optional
  /*newsapi.v2.sources({
        category: 'technology',
        language: 'en',
        country: 'us'
    }).then(response => {
        console.log(response);
        setNews(response.network.stations)
    });*/
  return (
    <div className="pages">
      <h1>NewsApp</h1>
      <NewsArticle article={news} />
    </div>
  )
}
