import React from 'react'
import { Button, Form } from 'semantic-ui-react'
import Filter from 'bad-words'

//var Filter = require('bad-words'),


class SearchBar extends React.Component {
  constructor(props) {
    super(props)
    this.state = { searchTopic: '' }
  }
  handleChange = (event) => {
    this.setState({ searchTopic: event.target.value })
  }

  bad = new Filter()
  handleSubmit = (event) => {
    event.preventDefault()
    if (this.state.searchTopic === '') {
      alert("Search field can't be empty")
    } else if (
      this.state.searchTopic === bad.clean) {
        alert("bad")
        console.log();
    } else {
      this.props.searchForTopic(this.state.searchTopic)
      //this.setState({ searchTopic: "" })
      console.log(bad.clean(`${this.state.searchTopic}`));
    }
  }

  render() {
    return (
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <Form onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Input
              placeholder="Search topic"
              name="topic"
              value={this.state.searchTopic}
              onChange={this.handleChange}
            />
            <Button type="submit" color="green">
              Search
            </Button>
          </Form.Group>
        </Form>
      </div>
    )
  }
}

export default SearchBar
