import React from 'react'
import axios from 'axios'

class News extends React.Component {
  state = {
    articles: [],
    isLoading: true,
    errors: null,
  }

  getArticles() {
    axios
      .get(
        'http://newsapi.org/v2/everything?q=ai&apiKey=1147083ab9a3466d9332ac26b5a5c2d0'
      )
      .then((response) =>
        response.data.results.map((article) => ({
          date: `${article.publishedAt}`,
          title: `${article.title}`,
          url: `${article.url}`,
        }))
      )
      .then((articles) => {
        this.setState({
          articles,
          isLoading: false,
        })
      })
      .catch((error) => this.setState({ error, isLoading: false }))
  }

  componentDidMount() {
    this.getArticles()
  }

  render() {
    const { isLoading, articles } = this.state
    return (
      <React.Fragment>
        <h2>#AI</h2>
        <div>
          {!isLoading ? (
            articles.map((article) => {
              const { date, title, url } = article
              return (
                <div key={title}>
                  <p>{date}</p>
                  <p>{title}</p>
                  <p>{url}</p>
                </div>
              )
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    )
  }
}
export default News
