import React, { useEffect, useState } from 'react'

const api = {
  key: 'b8b339cb458fb8bc4985ef3cc4e552a3',
  base: 'https://api.openweathermap.org/data/2.5/',
}

export const Weather = () => {
  const [query, setQuery] = useState('')
  const [weather, setWeather] = useState({})
  const [weatherDef, setWeatherDef] = useState({})
  const [apiError, setapiError] = useState('')

  const search = (evt) => {
    if (evt.key === 'Enter') {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then((res) => res.json())
        .then((result) => {
          setWeather(result)
          setQuery('')
          console.log(result);
        }).catch((err) => setapiError(err.sys.message))
    }
  }

  useEffect(() => {
    fetch(
      `api.openweathermap.org/data/2.5/weather?q=Helsinki&appid={API key}${api.key}`
    )
      .then((res) => res.json())
      .then((result) => {
        setWeatherDef(result)
        console.log(result)
      })
  },[])

  const WeatherItem = {}

  // Formating date
  const dateFormat = () => {
    let date = new Intl.DateTimeFormat('en-GB', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: '2-digit',
      hour: 'numeric',
      minute: 'numeric',
    }).format()
    return date
  }

  // Handle user input in search field
  const handleFilterChange = (event) => {
    setQuery(event.target.value)
    console.log(event.target.value)
  }

  return (
    <div className="pages">
      <div
        className={
          typeof weather.main != 'undefined'
            ? weather.main.temp > 16
              ? 'weather warm'
              : 'weather cold'
            : 'weather'
        }
      >
        <main>
          <div className="search-box">
            <input
              type="text"
              className="search-bar"
              placeholder="Search..."
              onChange={handleFilterChange}
              value={query}
              onKeyPress={search}
            />
          </div>
          {typeof weather.main != 'undefined' ? (
            <div>
              <div className="location-box">
                <div className="location">
                  {weather.name}, {weather.sys.country}
                </div>
                <div className="date">{dateFormat()}</div>
              </div>
              <div className="weather-box">
                <div className="temp"> {Math.round(weather.main.temp)}°c</div>
                <div className="weather-description">
                  {weather.weather[0].main}
                </div>
              </div>
            </div>
          ) : (
            <div className="weather-empty"></div>
          )}
          <div>
            <div className="location-box">
              <div className="location">
                {weatherDef.name}, {weatherDef.sys}
              </div>
              <div className="date">{dateFormat()}</div>
            </div>
            <div className="weather-box">
              <div className="temp"> {Math.round(weatherDef.main)}°c</div>
              <div className="weather-description">
                {weatherDef.main}
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  )
}

export default Weather
