import React from 'react'
import { Grid, Header, Image, List } from 'semantic-ui-react'

const ArticleItem = (props) => {
  const { article } = props
  return (
    <List.Item style={{ padding: 30 }}>
      <Grid>
        <Grid.Column
        className="eleven"
          width={11}
          style={{
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'flex-start',
          }}
        >
          <Header as="h3">{article.title}</Header>
          <List.Description style={{ margin: '20px 0' }}>
            {article.description}
          </List.Description>
          <List bulleted horizontal>
            <List.Item className="imagehide">
              <a href={article.url} target="_blank" rel="noopener noreferrer">
                {article.source.name}
              </a>
            </List.Item>
            <List.Item>{article.publishedAt.split('T')[0]}</List.Item>
          </List>
        </Grid.Column>
        <Grid.Column className width={5}>
          <Image src={article.urlToImage} />
        </Grid.Column>
      </Grid>
    </List.Item>
  )
}

const ArticleList = (props) => {
  return (
    <List divided style={{ maxWidth: 900, margin: '0 auto' }}>
      {props.articles.map((article, index) => (
        <ArticleItem article={article} key={article.title + index} />
      ))}
    </List>
  )
}

export default ArticleList
